={{(function getOutputConnectionTypes(parameters) {
    if (parameters.operation === 'checkIfEvaluating') {
        return [
            { type: 'main', displayName: 'Evaluation' },
            { type: 'main', displayName: 'Normal' },
        ];
    }
    return [{ type: 'main' }];
