={{((parameters) => {
    const categories = parameters.categories?.categories ?? [];
    const fallback = parameters.options?.fallback;
    const ret = categories.map((cat) => {
        return { type: 'main', displayName: cat.category };
    });
    if (fallback === 'other')
        ret.push({ type: 'main', displayName: 'Other' });
    return ret;
})($parameter)}}
